-- Didn't work for some reason... Flipping the sign on the -0x7fffffff part made it give random numbers again instead of a fixed point, but they weren't the correct numbers
function lcm(r)
    local tempr = math.floor(r) * 0x41a7 + (math.floor(r) / 0x1f31d) * -0x7fffffff
    if tempr < 1 then
        tempr = tempr + 0x7fffffff
    end
    tempr = tempr % 2^32 -- Maybe?
    return tempr
end

-- Thanks GrandpaGameHacker for the poro reference
function lcm_poro(r)
    local hi = math.floor(r / 127773)
    local lo = r - hi * 127773
    local temp_r = 16807 * lo - 2836 * hi
    if temp_r <= 0 then
        temp_r = temp_r + 2147483647
    end
    return temp_r
end

function get_pw_index(message_index)
    return message_index % 2 == 0 and 1 or -1
end

function has_cave_background(x, y)
    return map_ng[y + 1][x + 1] == 1
end

function convert_chunk_coords(x, y, pw_index, world_chunk_length)
    local xoffset = 512.0*world_chunk_length/2 -- -17920
    local yoffset = 512.0*14
    local finalx = pw_index * 512.0 * world_chunk_length + x * 512.0 - xoffset + 48.0
    local finaly = y * 512.0 - yoffset + 64.0
    return {finalx, finaly}
end

function calculate_eye_message_positions(seed, current_pw, return_chunk_coords)
    return_chunk_coords = return_chunk_coords or false

    local positions = {}
    local r = bit.bxor(seed, 0xe4bc7e0)
    if r < 0 then
        r = r * -0.5
    end
    r = lcm_poro(r)
    --r88 = 0.0 -- Seems to be unused
    local r08 = r


    for message_index=0,8 do
        local pw_index = get_pw_index(message_index)
        if current_pw ~= pw_index then
            goto continue
        end
        -- Makes up to 1000 attempts to place the eyes in a chunk with the right background, probably never will have to make more than 40
        for attempts=0,999 do
            local y = lcm_poro(r08)
            local x = math.floor(y * 4.656612875e-10 * world_chunk_length)
            y = lcm_poro(y)
            r08 = y
            --r88 = math.floor(r08) >> 0x20 -- Uh... Isn't this always 0?
            y = math.floor(r08 * 4.656612875e-10 * world_chunk_height)

            -- Pretty sure this condition is guaranteed by the way this was set up
            if -1 < math.floor(x) and -1 < y and math.floor(x) < world_chunk_length and y < world_chunk_height then
                r = r08
                if has_cave_background(x, y) then
                    if return_chunk_coords then
                        -- This option makes it easier to search for seeds where the messages appear in specific biomes.
                        positions[#positions+1] = {x, y}
                    else
                        -- Convert from chunk coords to world coords
                        local coords = convert_chunk_coords(x, y, pw_index, world_chunk_length)
                        positions[#positions+1] = coords
                    end
                    --r88 = 0.0
                    r08 = r
                    break
                end
                --r88 = 0.0
                r08 = r
            end

            if attempts == 999 then
                positions[#positions+1] = false
            end
        end

        ::continue::
    end
    return positions
end

function display_eye_message_positions(seed)
    print("Eye message coordinates for seed " .. seed .. ":")
    local positions = calculate_eye_message_positions(seed, 1)
    for i, pos in ipairs(positions) do
            print("E" .. i .. ": (" .. pos[1] .. ", " .. pos[2] .. ")")
            if i <= 4 then
                local west_x = pos[1] - 71680.0
                print("W" .. i .. ": (" .. west_x .. ", " .. pos[2] .. ")")
            end
    end
end

if #arg > 0 then
    display_eye_message_positions(math.floor(tonumber(arg[1])))
else
    print("Please enter a seed. ")
    local seed = math.floor(io.read("*n"))
    display_eye_message_positions(seed)
end
